      title: "Special Topics in Modern Art",
      subject: "ART_HIST",
      number: "368-0",
      section: "1",
      meeting_days: "TuTh",
      start_time: "11:00",
      end_time: "12:20",
      room: { building_name: "Kresge Centennial Hall 2-343" },
